import React, { useState } from 'react';
import { parseFile } from '../utils/parseData';
import { calculateStats } from '../utils/calculateStats';

const Upload = ({ onComplete }) => {
    const [uploading, setUploading] = useState(false);
    const [error, setError] = useState('');
    const [userName, setUserName] = useState('');
    const [dragActive, setDragActive] = useState(false);

    const handleFile = async (file) => {
        if (!file) return;

        setUploading(true);
        setError('');

        try {
            const books = await parseFile(file);

            if (books.length === 0) {
                throw new Error('No books found in file. Please check your export.');
            }

            const stats = calculateStats(books, userName || 'Reader');
            onComplete(stats);
        } catch (err) {
            setError(err.message);
            setUploading(false);
        }
    };

    const handleFileInput = (e) => {
        const file = e.target.files?.[0];
        if (file) handleFile(file);
    };

    const handleDrag = (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (e.type === "dragenter" || e.type === "dragover") {
            setDragActive(true);
        } else if (e.type === "dragleave") {
            setDragActive(false);
        }
    };

    const handleDrop = (e) => {
        e.preventDefault();
        e.stopPropagation();
        setDragActive(false);

        const file = e.dataTransfer.files?.[0];
        if (file) handleFile(file);
    };

    return (
        <div className="upload-page">
            <div className="upload-content">
                <h2 className="upload-title">Upload Your Reading Data</h2>

                <div className="name-input-section">
                    <label htmlFor="userName">Your Name (optional)</label>
                    <input
                        id="userName"
                        type="text"
                        placeholder="Enter your name"
                        value={userName}
                        onChange={(e) => setUserName(e.target.value)}
                        className="name-input"
                    />
                </div>

                <div
                    className={`drop-zone ${dragActive ? 'drag-active' : ''}`}
                    onDragEnter={handleDrag}
                    onDragLeave={handleDrag}
                    onDragOver={handleDrag}
                    onDrop={handleDrop}
                >
                    <div className="drop-zone-content">
                        <div className="upload-icon">ðŸ“š</div>
                        <p className="drop-text">
                            {uploading ? 'Processing...' : 'Drag and drop your file here'}
                        </p>
                        <p className="drop-subtext">or</p>
                        <label htmlFor="file-upload" className="file-upload-label">
                            Browse Files
                        </label>
                        <input
                            id="file-upload"
                            type="file"
                            accept=".csv,.xlsx,.xls"
                            onChange={handleFileInput}
                            disabled={uploading}
                            style={{ display: 'none' }}
                        />
                        <p className="file-types">Accepts CSV or Excel files</p>
                    </div>
                </div>

                {error && (
                    <div className="error-message">
                        <strong>Error:</strong> {error}
                    </div>
                )}

                <div className="upload-tips">
                    <h3>Tips:</h3>
                    <ul>
                        <li>Export your Goodreads library: Library â†’ Import/Export â†’ Export Library</li>
                        <li>Make sure your file includes at least: Title, Author, My Rating, Date Read</li>
                        <li>Both CSV and Excel formats (.xlsx, .xls) are supported</li>
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default Upload;
